# write your app_a settings

import logging
import os

import sentry_sdk
from sentry_sdk.integrations.django import DjangoIntegration
from sentry_sdk.integrations.logging import LoggingIntegration

# All of this is already happening by default!
sentry_logging = LoggingIntegration(
    level=logging.INFO,  # Capture info and above as breadcrumbs
    event_level=logging.WARN  # Send errors as events
)

try:
    from current_sentry_release import RELEASE_VERSION
except ImportError:
    # default version
    RELEASE_VERSION = os.environ.get('STAGE', 'local')

sentry_sdk.init(
    dsn=os.environ.get("SENTRY_DSN"),
    environment=os.environ.get('STAGE', 'local'),
    integrations=[DjangoIntegration(), sentry_logging],
    release=RELEASE_VERSION,
    attach_stacktrace=True
)
