from ib_users.build.serializers.definitions.VerifyOTPRequest.VerifyOTPRequestSerializer import VerifyOTPRequestSerializer
from ib_users.build.serializers.definitions.VerifyOTPRequest.VerifyOTPRequestSerializer import VerifyOTPRequestType
from ib_users.build.serializers.definitions.Email.EmailSerializer import EmailSerializer
from ib_users.build.serializers.definitions.Email.EmailSerializer import EmailType

from django_swagger_utils.drf_server.utils.decorator.deserialize import deserialize

class VerifyResetPasswordOTPToEmailType(VerifyOTPRequestType, EmailType):
    def __init__(self, **validated_data):
        VerifyOTPRequestType.__init__(self, **validated_data)
        EmailType.__init__(self, **validated_data)
        

class VerifyResetPasswordOTPToEmailSerializer(VerifyOTPRequestSerializer, EmailSerializer):
    def create(self, validated_data):
        
        verifyOTPRequestSerializer, _ = deserialize(VerifyOTPRequestSerializer, validated_data, many=False, partial=True)
        validated_data.update(verifyOTPRequestSerializer.__dict__)
        
        emailSerializer, _ = deserialize(EmailSerializer, validated_data, many=False, partial=True)
        validated_data.update(emailSerializer.__dict__)
        
        return VerifyResetPasswordOTPToEmailType(**validated_data)
