class GetUserIdAndLimitResponseType(object):
    def __init__(self, user_id, referral_limit,  **kwargs):
        self.user_id = user_id
        self.referral_limit = referral_limit

    def __str__(self):
        from django_swagger_utils.drf_server.utils.server_gen.get_unicode_str import get_unicode_str
        return get_unicode_str(self).encode('utf-8')

    def __getitem__(self, item):
        return getattr(self, item)


class GetUserIdAndLimitResponseSerializer(serializers.Serializer):
    user_id = serializers.CharField()
    referral_limit = serializers.IntegerField()

    def create(self, validated_data):
        return GetUserIdAndLimitResponseType(**validated_data)
