from rest_framework import serializers

from django_swagger_utils.drf_server.utils.decorator.deserialize import deserialize
from django_swagger_utils.drf_server.utils.server_gen.type_file_utils import get_type_object
from django_swagger_utils.drf_server.utils.server_gen.type_file_utils import get_type_list_object
from django_swagger_utils.drf_server.fields.collection_format_field import CollectionFormatField


class UserLoginResponseDefType(object):
    def __init__(self, access_token, user_id=None, refresh_token=None, expires_in=None,  **kwargs):
        self.access_token = access_token
        self.user_id = user_id
        self.refresh_token = refresh_token
        self.expires_in = expires_in

    def __str__(self):
        from django_swagger_utils.drf_server.utils.server_gen.get_unicode_str import get_unicode_str
        return get_unicode_str(self).encode('utf-8')

    def __getitem__(self, item):
        return getattr(self, item)


class UserLoginResponseDefSerializer(serializers.Serializer):
    access_token = serializers.CharField()
    user_id = serializers.CharField(required=False, allow_blank=True, allow_null=True)
    refresh_token = serializers.CharField(required=False, allow_blank=True, allow_null=True)
    expires_in = serializers.CharField(required=False, allow_blank=True, allow_null=True)

    def create(self, validated_data):
        return UserLoginResponseDefType(**validated_data)
