from ib_users.build.serializers.definitions.BaseUserProfile.BaseUserProfileSerializer import BaseUserProfileSerializer
from ib_users.build.serializers.definitions.BaseUserProfile.BaseUserProfileSerializer import BaseUserProfileType
from ib_users.build.serializers.definitions.UserProfile.UserProfile.Schema1.Schema1Serializer import Schema1Serializer
from ib_users.build.serializers.definitions.UserProfile.UserProfile.Schema1.Schema1Serializer import Schema1Type

from django_swagger_utils.drf_server.utils.decorator.deserialize import deserialize

class UserProfileType(BaseUserProfileType, Schema1Type):
    def __init__(self, **validated_data):
        BaseUserProfileType.__init__(self, **validated_data)
        Schema1Type.__init__(self, **validated_data)
        

class UserProfileSerializer(BaseUserProfileSerializer, Schema1Serializer):
    def create(self, validated_data):
        
        baseUserProfileSerializer, _ = deserialize(BaseUserProfileSerializer, validated_data, many=False, partial=True)
        validated_data.update(baseUserProfileSerializer.__dict__)
        
        schema1Serializer, _ = deserialize(Schema1Serializer, validated_data, many=False, partial=True)
        validated_data.update(schema1Serializer.__dict__)
        
        return UserProfileType(**validated_data)
