from ib_users.build.serializers.definitions.UserProfile.UserProfileSerializer import UserProfileSerializer
from ib_users.build.serializers.definitions.UserProfile.UserProfileSerializer import UserProfileType
from ib_users.build.serializers.definitions.UserProfileWithID.UserProfileWithID.Schema1.Schema1Serializer import Schema1Serializer
from ib_users.build.serializers.definitions.UserProfileWithID.UserProfileWithID.Schema1.Schema1Serializer import Schema1Type

from django_swagger_utils.drf_server.utils.decorator.deserialize import deserialize

class UserProfileWithIDType(UserProfileType, Schema1Type):
    def __init__(self, **validated_data):
        UserProfileType.__init__(self, **validated_data)
        Schema1Type.__init__(self, **validated_data)
        

class UserProfileWithIDSerializer(UserProfileSerializer, Schema1Serializer):
    def create(self, validated_data):
        
        userProfileSerializer, _ = deserialize(UserProfileSerializer, validated_data, many=False, partial=True)
        validated_data.update(userProfileSerializer.__dict__)
        
        schema1Serializer, _ = deserialize(Schema1Serializer, validated_data, many=False, partial=True)
        validated_data.update(schema1Serializer.__dict__)
        
        return UserProfileWithIDType(**validated_data)
