from enum import Enum
from django.conf import settings

OTP_LENGTH = 6
OTP_EXPIRY_TIME_IN_SEC = 300
OTP_MAX_RETRIES_COUNT = getattr(settings, "OTP_MAX_RETRIES_COUNT", 3)


class OTPStatus(Enum):
    NOT_REQUESTED = 'NOT_REQUESTED'
    EXPIRED = 'EXPIRED'
    ACTIVE = 'ACTIVE'


class VerificationType(Enum):
    LOGIN_WITH_OTP = "LOGIN_WITH_OTP"
    REGISTRATION_WITH_OTP = "REGISTRATION_WITH_OTP"
    VERIFY_PHONE_NUMBER_WITH_OTP = "VERIFY_PHONE_NUMBER_WITH_OTP"
    VERIFY_EMAIL_WITH_OTP = "VERIFY_EMAIL_WITH_OTP"
    RESET_PASSWORD_WITH_OTP = "RESET_PASSWORD_WITH_OTP"


class SendOTPThrough(Enum):
    CALL = 'CALL'
    SMS = 'SMS'
