from ib_users.exceptions.custom_exception_constants import ExceptionConstants


class UsernameAlreadyExistsException(Exception):
    def __init__(self,
                 message=ExceptionConstants.USERNAME_ALREADY_EXISTS_EXCEPTION):
        super().__init__(self, message)
        self.message = message


class AccountWithThisEmailAlreadyExistsException(Exception):
    def __init__(self,
                 message=
                 ExceptionConstants.ACCOUNT_ALREADY_EXISTS_WITH_THIS_EMAIL):
        super().__init__(self, message)
        self.message = message


class AccountWithThisPhoneNumberAlreadyExistsException(Exception):
    def __init__(self,
                 message=ExceptionConstants.
                 ACCOUNT_ALREADY_EXISTS_WITH_THIS_PHONE_NUMBER):
        super().__init__(self, message)
        self.message = message


class InvalidSocialDetailsException(Exception):
    def __init__(self, message):
        super().__init__(self, message)
        self.message = message


class AccountWithSocialDetailsAlreadyExistsException(Exception):
    def __init__(self, social_provider):
        super().__init__(self)
        self.social_provider = social_provider


class InvalidFBDetailsException(InvalidSocialDetailsException):
    def __init__(self):
        super().__init__(
            ExceptionConstants.INVALID_FB_DETAILS)


class AccountWithFBDetailsAlreadyExistsException(
    AccountWithSocialDetailsAlreadyExistsException):
    def __init__(self,
                 message=
                 ExceptionConstants.ACCOUNT_ALREADY_EXISTS_WITH_THIS_FB_DETAILS):
        super().__init__(message)


class InvalidTwitterDetailsException(InvalidSocialDetailsException):
    def __init__(self, message=ExceptionConstants.INVALID_TWITTER_DETAILS):
        super().__init__(message)


class AccountWithTwitterDetailsAlreadyExistsException(
    AccountWithSocialDetailsAlreadyExistsException):
    def __init__(self,
                 message=ExceptionConstants.
                 ACCOUNT_ALREADY_EXISTS_WITH_THIS_TWITTER_DETAILS):
        super().__init__(message)


class InvalidGoogleDetailsException(InvalidSocialDetailsException):
    def __init__(self, message=ExceptionConstants.INVALID_GOOGLE_DETAILS):
        super().__init__(message)


class AccountWithGoogleDetailsAlreadyExistsException(
    AccountWithSocialDetailsAlreadyExistsException):
    def __init__(self,
                 message=ExceptionConstants.
                 ACCOUNT_ALREADY_EXISTS_WITH_THIS_GOOGLE_DETAILS):
        super().__init__(message)
