from ib_users.exceptions.custom_exception_constants import \
    INVALID_TWITTER_ACCESS_TOKEN, INVALID_FB_ACCESS_TOKEN, \
    INVALID_GOOGLE_ACCESS_TOKEN
from ib_users.validators.base_validator import CustomException


class InvalidSocialAccessToken(CustomException):

    def __init__(self, message, error_type):
        super(InvalidSocialAccessToken, self).__init__(message, error_type)

    @classmethod
    def get_for_invalid_fb_token_exception(cls):
        return cls(
            message=INVALID_FB_ACCESS_TOKEN.message,
            error_type=INVALID_FB_ACCESS_TOKEN.code
        )

    @classmethod
    def get_for_invalid_google_token_exception(cls):
        return cls(
            message=INVALID_GOOGLE_ACCESS_TOKEN.message,
            error_type=INVALID_GOOGLE_ACCESS_TOKEN.code
        )

    @classmethod
    def get_for_invalid_twitter_token_exception(cls):
        return cls(
            message=INVALID_TWITTER_ACCESS_TOKEN.message,
            error_type=INVALID_TWITTER_ACCESS_TOKEN.code
        )
