from dataclasses import dataclass


@dataclass
class FacebookProfileDTO:
    id: str
    name: str
    birthday: str
    gender: str
    link: str


class FacebookInteractor:
    def __init__(self, facebook_provider):
        self.facebook_provider = facebook_provider

    def get_facebook_profile(self, access_token: str, presenter):
        facebook_profile = self.facebook_provider.get_facebook_profile(
            access_token=access_token)
        return presenter.get_facebook_profile(facebook_profile)
