from ib_users.interactors.login.base_login import BaseLogin
from ib_users.interactors.storages.user_accounts_storage import \
    PhoneNumberAndPasswordDTO, UserAccountsStorage
from ib_users.interactors.validators.phone_number_validator import \
    PhoneNumberValidator
from ib_users.validators import PasswordValidator


class LoginWithPhoneNumber(BaseLogin):

    def __init__(self,
                 storage: UserAccountsStorage,
                 phone_number_validator: PhoneNumberValidator):
        super().__init__(storage)
        self.phone_number_validator = phone_number_validator

    def get_user_id_for_given_login_data(self,
                                         phone_number_login_dto: PhoneNumberAndPasswordDTO):
        self.phone_number_validator.validate(
            phone_number_login_dto.phone_number_DTO)

        PasswordValidator.validate(phone_number_login_dto.password)

        user_id = self.storage.get_user_id_given_phone_number_and_password(
            phone_number_login_dto)
        super().validate_user_account_for_active_state(user_id)
        return user_id
