from dataclasses import dataclass

from ib_users.interactors.login.base_login import BaseLogin
from ib_users.interactors.storages.user_social_accounts_storage import \
    UserSocialAccountsStorage
from ib_users.interactors.third_party.social_account import SocialAccount


@dataclass
class TwitterLoginDTO:
    access_token: str
    secret_token: str


class LoginWithTwitter(BaseLogin):
    def __init__(self,
                 storage: UserSocialAccountsStorage,
                 twitter_service: SocialAccount):
        super().__init__(storage)
        self.twitter_service = twitter_service

    def get_user_id_for_given_login_data(self,
                                         twitter_login_dto: TwitterLoginDTO):
        social_id = self.twitter_service.get_social_id(
            access_token=twitter_login_dto.access_token,
            secret_token=twitter_login_dto.secret_token)
        user_id = self.storage.get_user_id_given_twitter_social_id(
            social_id=social_id)
        super().validate_user_account_for_active_state(user_id)
        return user_id
