from ib_users.interactors.registration.registration_helper import RegistrationHelper

from ib_users.validators import PasswordValidator


class UsernameRegistrationInteractor(RegistrationHelper):
    def create_account_with_username(self, username: str, password: str):
        self.validate_username(username)
        PasswordValidator.validate(password)
        user_id = self.accounts_storage.create_account_with_username(username,
                                                           password)
        return self.presenter.get_user_id(user_id)
