from dataclasses import dataclass


@dataclass
class TwitterProfileDTO:
    name: str = None
    profile_image_url_https: str = None


class TwitterInteractor():

    def __init__(self, provider, presenter):
        self.provider = provider
        self.presenter = presenter

    def get_twitter_user_profile_by_access_token(self, access_token):
        twitter_profile_dto = self.provider.get_user_profile(access_token=access_token)
        twitter_profile_response = self.presenter.get_user_profile(twitter_profile_dto)
        return twitter_profile_response
