from dataclasses import dataclass

from ib_users.interactors.DTOs.common_dtos import PhoneNumberDTO


@dataclass
class ResetPasswordWithOTPDTO:
    otp: str
    new_password: str


@dataclass
class ResetPasswordWithOTPUsernameDTO(ResetPasswordWithOTPDTO):
    username: str


@dataclass
class ResetPasswordWithOTPEmailDTO(ResetPasswordWithOTPDTO):
    email: str


@dataclass
class ResetPasswordWithOTPPhoneNumberDTO(PhoneNumberDTO,
                                         ResetPasswordWithOTPDTO):
    pass


@dataclass
class UserContactDetailsDTO(PhoneNumberDTO):
    email: str