from ib_users.interactors.presenters.user_credentials_presenter import \
    UserCredentialsPresenter
from ib_users.interactors.storages import UserAccountsStorage


class CheckDefaultPasswordIsChangedInteractor:
    def __init__(self, storage: UserAccountsStorage,
                 presenter: UserCredentialsPresenter):
        self.accounts_storage = storage
        self.presenter = presenter

    def is_default_password_changed(self, user_id: str):
        is_changed = self.accounts_storage.is_default_password_changed(
             user_id=user_id)
        return self.presenter.get_is_default_password_changed(is_changed)