from ib_users.constants.exception_constants import ExceptionConstants


class InvalidCurrentPasswordException(Exception):
    def __init__(self, message=ExceptionConstants.INVALID_CURRENT_PASSWORD):
        super(InvalidCurrentPasswordException, self).__init__(message)
        self.message = message


class InvalidNewPasswordException(Exception):
    def __init__(self, message=ExceptionConstants.INVALID_NEW_PASSWORD):
        super(InvalidNewPasswordException, self).__init__(message)
        self.message = message