from ib_users.interactors.storages.user_account_base_storage import UserAccountBaseStorage
from ib_users.models import UserAccount


class UserAccountSQLBaseStorage(UserAccountBaseStorage):

    def is_user_account_active(self, user_id):
        user_account = UserAccount.objects.get(user_id=user_id)
        return user_account.is_active
