import uuid

import pytest
from mock import create_autospec

from ib_users.exceptions.custom_exception_constants import \
    INVALID_FB_ACCESS_TOKEN, FB_NOT_LINKED
from ib_users.interactors.login.login_with_fb import LoginWithFB
from ib_users.interactors.storages.user_social_accounts_storage import \
    UserSocialAccountsStorage
from ib_users.interactors.third_party.social_account import SocialAccount
from ib_users.tests.interactors.login.login_tests_utils import LoginTestsUtils
from ib_users.utils.tests_utils import TestsUtils
from ib_users.validators.base_validator import \
    CustomException


class TestsLoginWithFB:
    def test_login_with_malformed_fb_access_token(self):
        fb_service = create_autospec(SocialAccount)
        fb_service.get_social_id.side_effect = \
            CustomException.from_exception_message_DTO(
                INVALID_FB_ACCESS_TOKEN)

        self._init_interactor("fb_access_token", fb_service)

        LoginTestsUtils.login_with_invalid_login_id(self.login_interactor, self.login_dto, INVALID_FB_ACCESS_TOKEN)
        fb_service.get_social_id.assert_called_once()

    @pytest.mark.parametrize("fb_access_token", ["fb_access_token", "access_token"])
    def test_login_with_not_registered_user(self, fb_access_token):
        self.init_interactor_with_side_effects(fb_access_token, FB_NOT_LINKED)

        with pytest.raises(CustomException) as not_registered_user:
            self.login_interactor.get_user_id_for_given_login_data(self.login_dto)

        TestsUtils.assert_custom_exception(FB_NOT_LINKED, not_registered_user)

    def test_login_with_correct_credentials(self):
        user_id = str(uuid.uuid4())
        self.init_interactor_with_return_value("fb_access_token", user_id)
        LoginTestsUtils.login_with_correct_credentials(self.login_interactor, self.login_dto, user_id)

    def test_login_with_deactivated_user(self):
        self._init_interactor("fb_access_token")
        LoginTestsUtils.login_with_deactivated_user(self.login_interactor, self.login_dto)

    def _init_interactor(self, access_token, social_login_service=None):
        storage_mock = create_autospec(UserSocialAccountsStorage)

        if social_login_service is None:
            social_login_service = create_autospec(SocialAccount)
        else:
            social_login_service = social_login_service

        self.login_interactor = LoginWithFB(storage_mock, fb_service=social_login_service)
        self.login_dto = access_token

    def init_interactor_with_side_effects(self, access_token, exception_message):
        storage_mock = create_autospec(UserSocialAccountsStorage)
        storage_mock.get_user_id_given_fb_social_id.side_effect = \
            CustomException.from_exception_message_DTO(exception_message)
        social_login_service = create_autospec(SocialAccount)

        self.login_interactor = LoginWithFB(storage_mock, fb_service=social_login_service)
        self.login_dto = access_token

    def init_interactor_with_return_value(self, access_token, return_value):
        storage_mock = create_autospec(UserSocialAccountsStorage)
        storage_mock.get_user_id_given_fb_social_id.return_value = return_value
        social_login_service = create_autospec(SocialAccount)

        self.login_interactor = LoginWithFB(storage_mock, fb_service=social_login_service)
        self.login_dto = access_token
