from ib_users.interactors.storages.user_accounts_storage import UserAccountDTO
from ib_users.presenters.user_account_json_presenter import \
    UserAccountJSONPresenter


def test_get_updated_user_account():
    user_id = '38434903'
    username = 'bob'
    user_account_json_presenter = UserAccountJSONPresenter()
    user_account_dto = UserAccountDTO(
        user_id=user_id,
        username=username
    )
    expected_user_account_json_response = {
        "user_id": str(user_id),
        "username": username
    }
    updated_user_account = user_account_json_presenter.get_user_account(
        user_account_dto)
    assert updated_user_account == expected_user_account_json_response
