import pytest

from ib_users.presenters.user_credentials_json_presenter import \
    UserCredentialsJSONPresenter


@pytest.mark.parametrize('is_changed', ['true', 'false'])
def test_get_is_default_password_changed(is_changed: bool):
    expected_json = {
        "is_default_password_changed" : is_changed
    }

    presenter = UserCredentialsJSONPresenter()
    actual_json = presenter.get_is_default_password_changed(is_changed)
    assert expected_json == actual_json