
def test_generate_new_referral_code():

    from ib_users.utils.referral_code_utils import generate_new_referral_code

    referral_code = generate_new_referral_code()

    from ib_users.constants.referral_constants import REFERRAL_CODE_LENGTH
    assert len(referral_code) == REFERRAL_CODE_LENGTH

    assert referral_code.isupper()