import pytest

from ib_users.exceptions.custom_exception_constants import \
    DEVICE_ID_CANNOT_BE_EMPTY
from ib_users.utils.tests_utils import TestsUtils
from ib_users.validators.base_validator import CustomException
from ib_users.validators.device_id_validator_impl import DeviceIdValidatorImpl


def test_non_empty_device_id():

    with pytest.raises(CustomException) as invalid_device_id_exception:
        DeviceIdValidatorImpl.validate("")
    TestsUtils.assert_custom_exception(
        DEVICE_ID_CANNOT_BE_EMPTY,
        invalid_device_id_exception)

def test_valid_device_id():
    DeviceIdValidatorImpl.validate("valid_device_id@#$")
