from ib_users.interactors.storages.user_accounts_storage import \
    UserEmailAndPhoneNumberDTO, PhoneNumberDTO


def are_email_and_phone_number_not_empty(
        email_and_phone_number_dto: UserEmailAndPhoneNumberDTO) -> bool:
    if email_and_phone_number_dto.email and \
            is_phone_number_not_empty(email_and_phone_number_dto.phone_number):
        return True

    return False


def is_phone_number_not_empty(phone_number: PhoneNumberDTO) -> bool:
    if phone_number.phone_number and phone_number.country_code:
        return True

    return False
