from django_swagger_utils.drf_server.utils.decorator.interface_decorator \
    import validate_decorator

from ib_users.interactors import LoginWithGoogle
from ib_users.storages.user_social_account_sql_storage import \
    UserSocialAccountsSQLStorage
from ib_users.third_party.social_account.google_account import GoogleAccount
from ib_users.utils.views.login_controllers_common import \
    validate_inputs_and_get_auth_tokens
from .validator_class import ValidatorClass


@validate_decorator(validator_class=ValidatorClass)
def api_wrapper(*args, **kwargs):
    request_data = kwargs['request_data']
    google_access_token = request_data["google_access_token"]
    login_interactor = LoginWithGoogle(storage=UserSocialAccountsSQLStorage(),
                                       google_service=GoogleAccount())
    return validate_inputs_and_get_auth_tokens(login_interactor,
                                               google_access_token)
